        {
            "type": "CR",
            "serial": "J",
            "number": "1191091",
            "amount": 39703.0,
            "balance": 39703.0,
            "currency": "PYG",
            "status": "PC",
            "expirationDate" : "2015-04-19",
            "billingInfo": {
                "cycle" : "D",
                "startDate": "2015-03-05",
                "endDate": "2015-04-04"
            },
            "contact" : {
                "name" : "courage the cowardly dog",
                "address" : "the middle of nowhere",
                "phone" : "1-800-COURAGE",
                "document" : 123456789,
                "documentType" : "RUC"
            }
        },{
            "type": "CR",
            "serial": "F",
            "number": "1827839",
            "amount": 64884.0,
            "balance": 0.0,
            "currency": "PYG",
            "status": "CA",
            "cycle" : "D",
            "expirationDate" : "2015-04-19",
            "billingInfo": {
                "cycle" : "D",
                "startDate": "2015-03-05",
